
new WOW().init();const slider=tns({container:'.promo__slider',items:1,slideBy:'page',autoplay:true,autoplayTimeout:5000,speed:700,controls:false,nav:true,navPosition:'bottom',autoplayHoverPause:true});document.querySelector('.promo .next').addEventListener('click',()=>{slider.goTo('prev');});document.querySelector('.promo .prev').addEventListener('click',()=>{slider.goTo('next');});const sliderMac=tns({container:'.solution__slider',items:1,slideBy:'page',speed:700,controls:false,nav:false,autoplayHoverPause:true});const $macLinks=document.querySelectorAll('.solution__link');document.querySelector('.solution .next').addEventListener('click',()=>{sliderMac.goTo('next');});document.querySelector('.solution .prev').addEventListener('click',()=>{sliderMac.goTo('prev');});for(let i=0;i<$macLinks.length;i++){$macLinks[i].addEventListener('click',(e)=>{e.preventDefault();sliderMac.goTo(i);});}
sliderMac.events.on('indexChanged',(e)=>{for(let j=0;j<$macLinks.length;j++){$macLinks[j].classList.remove('solution__link_active');}
if(e.index>0&&e.index<7){$macLinks[e.index-1].classList.add('solution__link_active');}});